export interface Breadcrumb {
  path?: string;
  absolutePath?: string;
  label: string;
  active: boolean;
  disabled: boolean;
}

import { Inject, Injectable } from '@angular/core';
import { Meta, MetaDefinition, Title } from '@angular/platform-browser';
import { Router } from '@angular/router';

export interface PageMetadata {
  title: string;
  // image will be added later
  description: string;
  author: string;
  keywords: string[];
  type: string;
}

const defaultMetadata: PageMetadata = {
  title: 'Ocean Premium',
  description: 'Ocean Premium',
  author: 'Ocean Premium',
  keywords: ['Water Toys'],
  type: 'website',
};

@Injectable()
export class MetadataService {
  constructor(
    private metaTagService: Meta,
    private titleService: Title,
    private router: Router
  ) {}

  public updateMetadata(
    metadata: Partial<PageMetadata>,
    index: boolean = true
  ): void {
    const pageMetadata: PageMetadata = { ...defaultMetadata, ...metadata };
    const metatags: MetaDefinition[] = this.generateMetaDefinitions(
      pageMetadata
    );

    this.metaTagService.addTags([
      ...metatags,
      { property: 'og:url', content: `https://oceanpremium.com${this.router.url}` },
      { name: 'robots', content: index ? 'index, follow' : 'noindex' },
      { name: 'viewport', content: 'width=device-width, initial-scale=1' },
      { 'http-equiv': 'Content-Type', content: 'text/html; charset=utf-8' },
    ]);

    this.titleService.setTitle(pageMetadata.title);
  }

  private generateMetaDefinitions(metadata: PageMetadata): MetaDefinition[] {
    return [
      { name: 'title', content: metadata.title },
      { property: 'og:title', content: metadata.title },

      { name: 'description', content: metadata.description },
      { property: 'og:description', content: metadata.description },

      { name: 'author', content: metadata.author },
      { property: 'og:author', content: metadata.author },

      { name: 'keywords', content: metadata.keywords.join(', ') },

      { property: 'og:type', content: metadata.type },
    ];
  }
}

export class City {
    name: string = "";
    countryCode: string = "";
    stateCode: string = "";
    latitude?: string = "";
    longitude?: string = "";
}

interface Timezone {
    zoneName: string;
    gmtOffset: string;
    gmtOffsetName: string;
    abbreviation: string;
    tzName: string;
}

export class Country {
    isoCode: string = "";
    name: string = "";
    phonecode: number = 0;
    flag: string = "";
    currency: string = "";
    latitude: number = 0;
    longitude: number = 0;
    timezones?: Timezone[] = [];
}

