import { isPlatformBrowser, isPlatformServer } from '@angular/common';
import { Inject, Injectable, PLATFORM_ID } from '@angular/core';
import { NgAuthService, NgUser } from '@appstrax/ng-auth';
import { User } from '../booking/models/user';

@Injectable({
  providedIn: 'root',
})
export class AuthService {
  private user!: User;
  private ngUser!: NgUser;

  constructor(
    private ngAuth: NgAuthService,
    @Inject(PLATFORM_ID) private platformId: Object
  ) {
    if (isPlatformBrowser(this.platformId) && this.isAuthenticated()) {
      this.ngUser = this.ngAuth.getAuthenticatedUser();
      this.user = User.fromNgUser(this.ngUser);
    }
  }

  isAuthenticated(): boolean {
    if (isPlatformServer(this.platformId)) return true;
    return this.ngAuth.isAuthenticated();
  }
}

import { isPlatformBrowser } from '@angular/common';
import { HttpClient } from '@angular/common/http';
import { Inject, Injectable, PLATFORM_ID } from '@angular/core';
import * as moment from 'moment';
import { Subject } from 'rxjs';

import { Product } from 'src/app/product/models/product.model';
import { AvailabilityGraph } from '../models/availability-graph.model';
import { Availability } from '../models/availability.model';
import { BookingLocation } from '../models/booking-location.model';
import { BookingPeriod } from '../models/booking-period.model';
import { Booking } from '../models/booking.model';

@Injectable({
  providedIn: 'root',
})
export class BookingService {
  bookings: Array<Booking> = [];
  bookingsChanged = new Subject();
  currentBooking: Booking | undefined;

  availabilityCheck = new Subject();

  apiBaseUrl: string =
    'https://zu6wjlgc49.execute-api.eu-west-1.amazonaws.com/staging/api/v1/products';

  constructor(
    private httpClient: HttpClient,
    @Inject(PLATFORM_ID) private platformId: Object
  ) {
    if (isPlatformBrowser(this.platformId)) {
      this.load();
    }
  }

  async addToBooking(booking: Booking, product: Product): Promise<Booking> {
    if (booking.id) {
      const bookingExists = this.bookings.find((b) => b.id === booking.id);
      if (!bookingExists) {
        this.createBooking(booking);
      }

      const exists = booking.products.find((p) => p.id === product.id);
      if (exists) {
        exists.quantity = exists.quantity + product.quantity;
      } else {
        booking.products.push(product);
      }

      this.bookingsChanged.next();
    } else {
      booking.id = this.uniqueId(2);
      booking.products.push(product);
      this.bookings.push(booking);
      this.bookingsChanged.next();
    }

    this.save();

    return booking;
  }

  createBooking(booking: Booking): Booking {
    this.bookings.push(booking);
    this.save();
    return booking;
  }

  async removeProduct(
    booking: Booking,
    product: Product
  ): Promise<Booking | undefined> {
    if (!booking) throw new Error('Booking required.');

    booking.products = booking.products.filter((p) => p.id !== product.id);
    if (booking.products.length === 0) {
      this.deleteBooking(booking);
      return undefined;
    }

    this.save();
    return booking;
  }

  async removeAccessory(
    booking: Booking,
    accessory: Product
  ): Promise<Booking> {
    if (!booking) throw new Error('Booking required.');
    booking.products.forEach((p) => {
      p.accessories = p.accessories.filter((a) => a.id !== accessory.id);
    });
    this.save();
    return booking;
  }

  async addAccessories(
    pBooking: Booking,
    pProduct: Product,
    accessories: Array<Product>
  ): Promise<Booking> {
    const booking = this.bookings.find((b) => b.id === pBooking.id);
    if (!booking) throw new Error('Booking not found');

    const product = booking.products.find((p) => p.id === pProduct.id);
    if (!product) throw new Error('Product not found');

    product.accessories = accessories;
    this.bookingsChanged.next();

    this.save();

    return booking;
  }

  async changeProductQuantity(
    booking: Booking,
    product: Product,
    quantity: number
  ): Promise<Booking | undefined> {
    if (quantity === 0) {
      return this.removeProduct(booking, product);
    }

    const prod = booking.products.find((p) => p.id === product.id);
    if (!prod) throw new Error('Product not found');

    prod.quantity = quantity;

    this.save();

    return booking;
  }

  async changeAccessoryQuantity(
    booking: Booking,
    accessory: Product,
    quantity: number
  ): Promise<Booking> {
    if (quantity === 0) {
      return this.removeAccessory(booking, accessory);
    }

    booking.products.forEach((p) => {
      const acc = p.accessories.find((a) => a.id === accessory.id);
      if (!acc) throw new Error('Accessory not found');

      acc.quantity = quantity;
    });

    this.save();

    return booking;
  }

  getBookings() {
    return [...this.bookings.filter((b) => !b.paid)];
  }

  getBookingById(bookingId: string | null) {
    return this.bookings.find((b) => b.id == bookingId);
  }

  deleteBooking(booking: Booking): Array<Booking> {
    this.bookings = this.bookings.filter((b) => b.id !== booking.id);
    this.save();
    this.bookings = this.bookings.filter((b) => b.paid === false);
    this.bookingsChanged.next();
    return this.bookings;
  }

  getCurrentBooking(): Booking | undefined {
    return this.currentBooking;
  }

  setCurrentBooking(booking: Booking) {
    this.currentBooking = booking;
    this.bookingsChanged.next();
  }

  async productsAvailability(booking: Booking): Promise<Availability> {
    const request: Array<any> = [];
    if (!booking) {
      return new Availability();
    }

    booking.products.map((product) => {
      request.push({
        id: product.id,
        quantity: product.quantity,
        location: booking.location,
        period: {
          start: booking.period.start,

          /*moment(booking.period.fromDate)
            .utc()
            .endOf('day')
            .format('YYYY-MM-DDTHH:mm:ss.000Z')*/ end:
            booking.period.end,
          /*moment(booking.period.toDate)
            .utc()
            .endOf('day')
            .format('YYYY-MM-DDTHH:mm:ss.000Z')*/
        },
        accessories: product.accessories,
      });
    });

    return new Promise((resolve, reject) => {
      this.httpClient
        .post<any>(`${this.apiBaseUrl}/availability`, request)
        .subscribe(
          (res) => {
            // TODO: pick up availability state
            booking.availability = res.data as Availability;
            booking.checkedAvailabilityOn = new Date();
            this.save();
            resolve(res.data as Availability);
          },
          (err) => reject(err)
        );
    });
  }

  async availabilityGraph(
    product: Product,
    period: BookingPeriod,
    location: BookingLocation
  ): Promise<AvailabilityGraph> {
    if (!product) throw new Error('Product required');
    if (!period) throw new Error('Period required');
    if (!location) throw new Error('Location required');

    const request = {
      id: product.id,
      quantity: product.quantity,
      location,
      period,
    };

    return new Promise((resolve, reject) => {
      this.httpClient
        .post<any>(`${this.apiBaseUrl}/availability-graph`, request)
        .subscribe(
          (res) => {
            resolve(res.data as AvailabilityGraph);
          },
          (err) => reject(err)
        );
    });
  }

  // https://stackoverflow.com/questions/52836247/how-to-generate-uuid-in-angular
  private uniqueId(parts: number): string {
    const stringArr = [];
    for (let i = 0; i < parts; i++) {
      // tslint:disable-next-line:no-bitwise
      const S4 = (((1 + Math.random()) * 0x10000) | 0)
        .toString(16)
        .substring(1);
      stringArr.push(S4);
    }
    return stringArr.join('-');
  }

  save() {
    localStorage.setItem('BOOKINGS', JSON.stringify(this.bookings));
  }

  private load() {
    this.bookings = [];

    try {
      const jsonBookings = JSON.parse(
        localStorage.getItem('BOOKINGS') || '[]'
      ) as Array<any>;

      jsonBookings.forEach((jsonBooking) => {
        const booking = Booking.fromJson(jsonBooking);
        this.bookings.push(booking);
      });
    } catch (err) {
      console.log('Failed to parse Bookings from Local Storage', err);
    }
  }

  forceAvailabilityCheck() {
    this.availabilityCheck.next(true);
  }
}

import { Injectable } from '@angular/core';
import { environment } from 'src/environments/environment';
import { City, Country } from '../models/countries.model';
import { HttpService } from '../../core/services/http.service';

@Injectable({
    providedIn: 'root',
})
export class CountryService {

    constructor(private httpService: HttpService) { }

    async getAllCountries(): Promise<Array<Country>> {
        const response = await this.httpService.get(`${environment.apiBaseUrl}/country-state-city`);
        if (!response.data) {
            throw new Error('Countries not found');
        }
        return response.data.map((d: any) => {
            const country = new Country();

            country.isoCode = d.isoCode;
            country.name = d.name;
            country.phonecode = d.phonecode;
            country.flag = d.flag;
            country.currency = d.currency;
            country.latitude = d.latitude;
            country.longitude = d.longitude;
            country.timezones = d.timezones;

            return country;
        });
    }

    async getCitiesOfCountry(countryCode: string): Promise<Array<City>> {
        const response = await this.httpService.get(`${environment.apiBaseUrl}/country-state-city/${countryCode}/City`);
        if (!response.data) {
            throw new Error('Countries not found');
        }
        return response.data.map((d: any) => {
            const city = new City();

            city.name = d.name;
            city.countryCode = d.countryCode;
            city.stateCode = d.stateCode;
            city.latitude = d.latitude;
            city.longitude = d.longitude;

            return city;
        });
    }
}

import { Inject, Injectable, PLATFORM_ID } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { NgAuthService } from '@appstrax/ng-auth';

@Injectable()
export class HttpService {
  constructor(
    private httpClient: HttpClient,
    private ngAuth: NgAuthService,
    // @Inject(PLATFORM_ID) private platformId: Object
  ) {

  }

  public post(url: string, body: any, apiKey?: string): Promise<any> {
    return new Promise((resolve, reject) => {
      this.httpClient.post(url, body, this.getHeaders(apiKey)).subscribe(
        (response) => resolve(response),
        (err) => reject(err)
      );
    });
  }

  public put(url: string, body: any, apiKey?: string): Promise<any> {
    return new Promise((resolve, reject) => {
      this.httpClient.put(url, body, this.getHeaders(apiKey)).subscribe(
        (response) => resolve(response),
        (err) => reject(err)
      );
    });
  }

  public patch(url: string, body: any, apiKey?: string): Promise<any> {
    return new Promise((resolve, reject) => {
      this.httpClient.patch(url, body, this.getHeaders(apiKey)).subscribe(
        (response) => resolve(response),
        (err) => reject(err)
      );
    });
  }

  public get(url: string, apiKey?: string): Promise<any> {
    return new Promise((resolve, reject) => {
      this.httpClient.get(url, this.getHeaders(apiKey)).subscribe(
        (response) => resolve(response),
        (err) => reject(err)
      );
    });
  }

  public delete(url: string, apiKey?: string): Promise<any> {
    return new Promise((resolve, reject) => {
      this.httpClient.delete(url, this.getHeaders(apiKey)).subscribe(
        (response) => resolve(response),
        (err) => reject(err)
      );
    });
  }

  public getText(url: string, apiKey?: string): Promise<any> {
    return new Promise((resolve, reject) => {
      const headers: any = this.getHeaders(apiKey);
      headers.responseType = 'text';

      this.httpClient.get(url, headers).subscribe(
        (response) => resolve(response),
        (err) => reject(err)
      );
    });
  }

  public getHeaders(apiKey?: string): { headers: any } {
    let headers = new HttpHeaders();

    // headers = headers.append('X-Api-Key', apiKey || environment.apiKey);
    // headers = headers.append('X-Api-User-Id', apiKey || environment.apiKey);
    // if (isPlatformServer(this.platformId)) {
    //   return { headers }
    // } else {
    if (this.ngAuth.getAuthToken()) {
      //   headers = headers.append('X-User-Auth-Token', this.ngAuth.getAuthToken());
      headers = headers.append('Authorization', 'Bearer ' + this.ngAuth.getAuthToken());
    }
    // }
    return { headers };
  }
}

import { Injectable } from '@angular/core';
import { environment } from 'src/environments/environment';
import { Location } from '../models/location.model';
import { HttpService } from './http.service';

@Injectable({
  providedIn: 'root',
})
export class LocationService {

  constructor(private httpService: HttpService) {}

  async getLocations(): Promise<Array<Location>> {
    const response = await this.httpService.get(`${environment.apiBaseUrl}/locations`);
    if (!response.data) {
      throw new Error('Locations not found');
    }
    return response.data.map((d: any) => {
      const location = new Location();
      location.id = d.id;
      location.cityArea = d.cityArea;
      location.cruisingArea = d.cruisingArea;
      location.name = d.name;
      location.timezone = d.timezone;
      return location;
    });
  }

  async getPopularMediterraneanPorts(
    locations: Array<Location>
  ): Promise<Array<Location>> {
    const popularLocationNames = [
      'Cannes',
      'Monaco',
      'Naples',
      'Porto Cervo',
      'Athens',
      'Split',
    ];

    return popularLocationNames.map((popularName: string, index: number) => {
      const exists = locations.find(
        (location: Location) => location.name === popularName
      );

      // Temp: return random location, should always be more than the 6 popular ones
      if (!exists) {
        return locations[index];
      }

      return exists;
    });
  }

  async getPopularCaribbeanPorts(
    locations: Array<Location>
  ): Promise<Array<Location>> {
    const popularLocationNames = [
      'Saint Maarten',
      'Antigua',
      'BVI',
      'USVI',
      'Nassau',
      'Fort Lauderdale',
    ];

    return popularLocationNames.map((popularName: string, index: number) => {
      const exists = locations.find(
        (location: Location) => location.name === popularName
      );

      // Temp: return random location, should always be more than the 6 popular ones
      if (!exists) {
        return locations[index];
      }

      return exists;
    });
  }
}

