  queryParams = `${queryParams}&per_page=100`;
  queryParams = `${queryParams}&q[m]=or`;

  let urls: Array<string> = [];
  let newParams = '' + queryParams;
  let index = 0;
  availabilityRequest.products.forEach((p: any) => {
