              lw.warehouseId === wh.id &&
              lw.locationId === deliveryLocation!.id &&
              lw.warehouseTypeId === grayWarehouseType.id
          )
          .sort(sortByDelayInHours)
          .sort(sortByPrice);
        if (grays.length > 0) {
          s.score = 2;
          s.locationWarehouse = grays[0];
