          s.type = 'GRAY';

          if (s.rentalQuantityAvailable === 0) {
            s.score = s.score + 2;
          }
          return;
        }

        const checks = locationWarehouses
          .filter(
            (lw) =>
              lw.warehouseId === wh.id &&
              lw.locationId === deliveryLocation!.id &&
