      if (hoursToDeliveryFromNow < p.store.locationWarehouse.delayInHours) {
        p.availabilityState = 'AVAILABLE_BUT_DELAYED';

        if (p.store.type === 'NATIVE') {
          p.note = note1;
        }

        if (p.store.type === 'ALTERNATIVE') {
          p.note = `${note1} ${note2}`;
        }

        if (p.store.type === 'GRAY' || p.store.type === 'CHECK') {
