      method: 'GET',
      url: `https://zu6wjlgc49.execute-api.eu-west-1.amazonaws.com/staging/api/v1/products/inventory${params}`,
    });
    if (res.status === 200) {
      inventory = res.data.data;
    }
  } catch (err) {
    return res
      .json({
        success: false,
        message: 'Failed to fetch inventory.',
        data: [],
      })
      .status(200);
  }

  try {
