      ProductSlugIdMapCache.get(key)
    );
    const fuse = new Fuse(products, {
      keys: ['name', 'seoFriendlyName', 'seoMetaDescription'],
    });
    const data = req.query.search
      ? fuse.search(req.query.search)
      : products.map((p) => {
        return {
          item: p,
        };
      });

    const kitIds = [
      87,
      98,
      122,
      125,
      126,
      127,
      131,
